#' @export
anova <- function(
    estype = "f",
    es = 0.3,
    power = 0.8,
    n = 20,
    k = 3,
    alpha = 0.05,
    powerDist = TRUE,
    powerCurveES = FALSE,
    powerCurveN = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("anova requires jmvcore to be installed (restart may be required)")


    options <- anovaOptions$new(
        estype = estype,
        es = es,
        power = power,
        n = n,
        k = k,
        alpha = alpha,
        powerDist = powerDist,
        powerCurveES = powerCurveES,
        powerCurveN = powerCurveN)

    analysis <- anovaClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

