    "muANOVABase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "jpower",
                name = "muANOVA",
                version = c(1,0,0),
                options = options,
                results = muANOVAResults$new(options=options),
