        }))

#' ANOVA (imputed means)
#'
#' 
#' @param data .
#' @param dep .
#' @param lev_fac_a .
#' @param lev_fac_b .
#' @param lev_fac_c .
#' @param type_fac_a .
#' @param type_fac_b .
#' @param type_fac_c .
#' @param n .
#' @param alpha .
#' @param stdev .
#' @param withcorr .
#' @param num_facs .
#' @param powerDist .
#' @param powerCurveES .
#' @param powerCurveN .
#' @param power .
#' @param findN .
#' @param min_n .
#' @param max_n .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$intro} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$designtab} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$DesPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$text1} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$main} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$text2} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$tabN} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$powerDist} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$distText} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$powerCurveES} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$powerCurveN} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$designtab$asDF}
#'
#' \code{as.data.frame(results$designtab)}
#'
#' @export
muANOVA <- function(
    data,
    dep,
    lev_fac_a = 3,
    lev_fac_b = 2,
    lev_fac_c = 2,
    type_fac_a = "b",
    type_fac_b = "b",
    type_fac_c = "b",
    n = 20,
    alpha = 0.05,
    stdev = 1,
    withcorr = 0,
    num_facs = "one",
    powerDist = FALSE,
    powerCurveES = FALSE,
    powerCurveN = FALSE,
    power = 0.8,
    findN = FALSE,
    min_n = 3,
    max_n = 100) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("muANOVA requires jmvcore to be installed (restart may be required)")

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL))


    options <- muANOVAOptions$new(
        dep = dep,
        lev_fac_a = lev_fac_a,
        lev_fac_b = lev_fac_b,
        lev_fac_c = lev_fac_c,
        type_fac_a = type_fac_a,
        type_fac_b = type_fac_b,
        type_fac_c = type_fac_c,
        n = n,
        alpha = alpha,
        stdev = stdev,
        withcorr = withcorr,
        num_facs = num_facs,
        powerDist = powerDist,
        powerCurveES = powerCurveES,
        powerCurveN = powerCurveN,
        power = power,
        findN = findN,
        min_n = min_n,
        max_n = max_n)

    analysis <- muANOVAClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

